import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import 'normalize.css';
import 'reset.css';

class TodoInput extends Component {
  render(){
    return (
      <div>
        <input type="text" DefaultValue={this.props.content}/>
      </div>
    )
  }
}

function TodoIntem() {
  return (
    <div>

    </div>
  )
}

class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      newTodo: '',
      todoList: [
        {id:1,title:'第一个待办',status:'conpleted',deleted:'false'},
        {id:2,title:'第二个待办',status:'conpleted',deleted:'false'},
        {id:2,title:'第三个待办',status:'conpleted',deleted:'false'}

      ]
    }
  }

  render() {
    let todos = this.state.todoList.map((item,index)=>{
      return <li>{item.title}</li>
    })

    return (
      <div className="App App-header">
        <header>
        <h1 className="hello">我的待办</h1>
        <div className="inputWraper">
          <TodoInput content={this.state.newTodo}/>
          <TodoIntem/>
        </div>
        <ol>
        {todos}
        </ol>
        </header>
      </div>
    );
  }
}

export default App;
