import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import 'normalize.css';
import 'reset.css';

class TodoInput extends Component {
  render(){
    return (
      <div>
        <input type="text" defaultValue={this.props.content}
        onKeyPress={this.submit.bind(this)} className='TodoInput'/>
      </div>
    )
  }
  submit(e){
    console.log(e)
    if(e.key === 'Enter') {
      this.props.onSubmit(e)
    }
  }
}

class TodoItem extends Component {

  toggle(e){
    this.props.onToggle(e,this.props.todo)
  }
  delete(e){
    this.props.onDelete(e,this.props.todo)
  }
  render(){

    return (
      <div>
        <input type='checkbox' checked={this.props.todo.status === 'completed'} 
        onChange={this.toggle.bind(this)}/> {this.props.todo.title}
        <button onClick={this.delete.bind(this)}>删除</button>
      </div>
    )
  }
}

class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      newTodo: '',
      todoList: [
        {id:1,title:'第一个待办',status:'completed',deleted:'false'}      
      ]
    }
  }


  addTodo(event){
    this.state.todoList.push({
      id: idMaker,
      title: event.target.value,
      status: null,
      deleted: false
    })
    this.setState({
      newTodo: '',
      todoList: this.state.todoList
    })
  }

  toggle(e,todo){
    todo.status = todo.status === 'completed'? '': 'completed'
    this.setState(this.state)
  }

  delete(event,todo){
    todo.deleted = true
    this.setState(this.state)
  }


  render() {
    let todos = this.state.todoList.filter((item)=> !item.deleted)
    .map((item,index)=>{
      return (
        <li key={index}>
          <TodoItem todo={item} onToggle={this.toggle.bind(this)}
          onDelete={this.delete.bind(this)}/>
        </li>)
    })
    console.log(todos)

    return (
      <div className="App App-header">
        <header className="contains">
          <h1 className="hello">我的待办</h1>
          <div className="inputWrapper">
            <TodoInput content={this.state.newTodo} onSubmit={this.addTodo.bind(this)} />
          </div>
          <ol className='itemList'>
          {todos}
          </ol>
        </header>
      </div>
    );
  }
}

export default App

let id = 0
function idMaker(){
  id += 1
  return id
}
